import React,{useState} from 'react';
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expense.css";
import ExpenseFilter from '../NewExpense/ExpenseFilter';
function Expense(usingpops) {
   const [Year,SetYear] = useState('2020');  //ise newdata function ke andr nhi likh skte baahr hi likhna pdega
  function Newdata(dataentered) {
    SetYear(dataentered);
  // console.log('expense.js');
  // console.log(dataentered);

  }
  return (
    <div>
      <ExpenseFilter fromexpense={Newdata} />
    <Card className="expenses">
      <ExpenseItem
        title={usingpops.expense[0].title}
        amount={usingpops.expense[0].amount}
        date={usingpops.expense[0].date}
      />
      <ExpenseItem
        title={usingpops.expense[1].title}
        amount={usingpops.expense[1].amount}
        date={usingpops.expense[1].date}
      />
      <ExpenseItem
        title={usingpops.expense[2].title}
        amount={usingpops.expense[2].amount}
        date={usingpops.expense[2].date}
      />
      <ExpenseItem
        title={usingpops.expense[3].title}
        amount={usingpops.expense[3].amount}
        date={usingpops.expense[3].date}
      />
    </Card>
    </div>
  );
}
export default Expense;
