import "./ExpenseForm.css";
import React,{useState} from "react";
import { getCombinedModifierFlags } from "typescript";

function ExpenseForm() {

    const [enteredtitle,setEnteredTitle]=useState('')
    const [enteredamount,setEnteredAmount]=useState('')
    const [enteredDate,setEnteredDate]=useState('')

    // instead of making three sets we can make one state comprising all of them 
    // const [userInput,setUserInput] = useState({
    //     enteredtitle: '',
    //     enteredamount: '',
    //     entereddate: '',
    // })

    const titlechanger= (event) => {
        setEnteredTitle(event.target.value);

        // for three states combined
        // setEnteredTitle({
        //     ...userInput,
        //     enteredtitle: event.target.value,
        // })
    }
    const amountchanger= (event) => {
        setEnteredAmount(event.target.value);
    }
    const datechanger= (event) => {
        setEnteredDate(event.target.value);
        console.log(event.target.value);
    }
  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
            <label>Title</label>
            <input type="text" onChange={titlechanger} />
        </div>
        <div className="new-expense__control">
            <label>Amount</label>
            <input type="number" min="0.01" step="0.01" onChange={amountchanger} />
        </div>
        <div className="new-expense__control">
            <label>Date</label>
            <input type="date" min="2019-01-01" max="2022-12-31" onChange={datechanger} />
        </div>
      </div>

      <div className="new-expense__actions">
          <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}

export default ExpenseForm;
