import React from 'react';

import './ExpenseFilter.css';

const ExpensesFilter = (props) => {

    function transfer(event){
        // props.fromexpense(event.)
        console.log(event);
    }

  return (
    <div className='expenses-filter'>
      <div className='expenses-filter__control'>
        <label>Filter by year</label>
        <select>
          <option value='2022' onChange={transfer}>2022</option>
          <option value='2021' onClick={transfer}>2021</option>
          <option value='2020' onClick={transfer}>2020</option>
          <option value='2019' onClick={transfer}>2019</option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
